#include <stdio.h>
#include <stdlib.h>

typedef struct node{
  int num;
  struct node* left;
  struct node* right;
}NODE;

void insert(NODE** p,int data){
  if(*p == NULL){
	*p = (NODE*)malloc(sizeof(NODE));
	(*p)->num = data;
	(*p)->left = NULL;
	(*p)->right = NULL;
	return;
  }else{
	if((*p)->num > data){
	  insert(&((*p)->left),data);
	}else{
	  insert(&((*p)->right),data);
	}
  }
}

int main(){
	int i,data[10] = {81,4,62,95,21,16,84,3,40,91};
  NODE* root = NULL,*check;
  for(i = 0;i < 10;i++){
	insert(&root,data[i]);
  }
  check = root;
  while(check->left != NULL){
	check = check->left;
  }
  printf("%d",check->num);
	return 0;
}
